const fullMessage = $input.first().json.message?.text || '';
const content = fullMessage.replace('/post', '').trim();

const chatId = $input.first().json.message?.chat?.id;
const messageId = $input.first().json.message?.message_id;
const userName = $input.first().json.message?.from?.first_name || 'User';

if (!content || content.length < 100) {
  return {
    error: true,
    errorType: 'TOO_SHORT',
    message: 'ê¸€ì´ ë„ˆë¬´ ì§§ìŠµë‹ˆë‹¤. ìµœì†Œ 100ì ì´ìƒ ì‘ì„±í•´ì£¼ì„¸ìš”.',
    chatId,
    messageId
  };
}

if (content.length > 50000) {
  return {
    error: true,
    errorType: 'TOO_LONG',
    message: 'ê¸€ì´ ë„ˆë¬´ ê¹ë‹ˆë‹¤. 50,000ì ì´ë‚´ë¡œ ì‘ì„±í•´ì£¼ì„¸ìš”.',
    chatId,
    messageId
  };
}

return {
  error: false,
  content,
  chatId,
  messageId,
  userName,
  timestamp: new Date().toISOString(),
  contentLength: content.length
};

// Enhanced AI ì‘ë‹µ íŒŒì‹± with ë‹¤ì¤‘ ì´ë¯¸ì§€ ì§€ì›
const aiContent = $input.first().json.choices[0].message.content;
let aiResponse;

try {
  const cleanContent = aiContent.replace(/```json|```/gi, '').trim();
  const start = cleanContent.indexOf('{');
  const end = cleanContent.lastIndexOf('}');

  if (start === -1 || end === -1) {
    throw new Error('JSON êµ¬ì¡°ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤');
  }

  const jsonString = cleanContent.substring(start, end + 1);
  aiResponse = JSON.parse(jsonString);
} catch (e) {
  throw new Error('AI ì‘ë‹µ íŒŒì‹± ì‹¤íŒ¨: ' + e.message);
}

const originalData = $('Extract and Validate Content').first().json;

// URL ìƒì„±
const generateSlug = (recommended, title) => {
  const base = recommended || title || 'post-' + Date.now();
  return base.toLowerCase().replace(/[^a-z0-9-]/g, '-').replace(/--+/g, '-').replace(/^-|-$/g, '').substring(0, 100);
};

// ë°ì´í„° ì •ë¦¬
const blogData = {
  title: aiResponse.title || 'ì œëª© ì—†ìŒ',
  slug: generateSlug(aiResponse.recommended_url, aiResponse.title),
  excerpt: (aiResponse.excerpt || '').substring(0, 160),
  meta_title: (aiResponse.meta_title || aiResponse.title || '').substring(0, 60),
  meta_description: (aiResponse.meta_description || '').substring(0, 160),
  tags: Array.isArray(aiResponse.tags) ? aiResponse.tags.slice(0, 10) : [],
  primary_tag: aiResponse.primary_tag || 'General',
  content_html: aiResponse.content_html || '<p>ë‚´ìš© ì—†ìŒ</p>',
  content_markdown: aiResponse.content_markdown || originalData.content,
  feature_image_prompt: aiResponse.feature_image_prompt || 'Blog hero image',

  // ë‹¤ì¤‘ ì´ë¯¸ì§€ ì²˜ë¦¬
  inline_images: Array.isArray(aiResponse.inline_images) ? aiResponse.inline_images.slice(0, 5) : [],

  has_code_blocks: Boolean(aiResponse.has_code_blocks),
  estimated_read_time: parseInt(aiResponse.estimated_read_time) || Math.ceil(originalData.contentLength / 500),
  chatId: originalData.chatId,
  messageId: originalData.messageId,
  userName: originalData.userName,
  timestamp: originalData.timestamp
};

// Notion ë¸”ë¡ ë¶„í• 
const NOTION_BLOCK_LIMIT = 2000;
const content = blogData.content_markdown;

if (content.length <= NOTION_BLOCK_LIMIT) {
  blogData.content_chunks = [content];
  blogData.content_preview = content;
  blogData.is_long_content = false;
  blogData.total_chunks = 1;
} else {
  const chunks = [];
  const paragraphs = content.split('\n\n');
  let currentChunk = '';

  for (const para of paragraphs) {
    if ((currentChunk + '\n\n' + para).length > NOTION_BLOCK_LIMIT) {
      chunks.push(currentChunk.trim());
      currentChunk = para;
    } else {
      currentChunk += (currentChunk ? '\n\n' : '') + para;
    }
  }

  if (currentChunk) chunks.push(currentChunk.trim());

  blogData.content_chunks = chunks;
  blogData.content_preview = chunks[0];
  blogData.is_long_content = true;
  blogData.total_chunks = chunks.length;
}

// ì´ë¯¸ì§€ ìƒì„± ì¤€ë¹„
blogData.total_images = 1 + blogData.inline_images.length; // Feature + Inline images
blogData.has_inline_images = blogData.inline_images.length > 0;

return blogData;

// ì¸ë¼ì¸ ì´ë¯¸ì§€ í”„ë¡¬í”„íŠ¸ ì¤€ë¹„
const blogData = $('Process Enhanced Blog Data').first().json;
const featureImageData = $('Generate Feature Image').first().json;

// Feature image URL ì¶”ì¶œ
const featureImageUrl = featureImageData.data?.[0]?.url || '';

// ì¸ë¼ì¸ ì´ë¯¸ì§€ ë°°ì—´ì„ ê°œë³„ ì•„ì´í…œìœ¼ë¡œ ë¶„í•  ì¤€ë¹„
const inlineImages = blogData.inline_images || [];

return inlineImages.map((img, index) => ({
  ...img,
  index: index + 1,
  totalImages: inlineImages.length,
  blogData: {
    title: blogData.title,
    slug: blogData.slug,
    notionPageId: $('Create Notion Page').first().json.id,
    featureImageUrl
  }
}));

// ìƒì„±ëœ ì´ë¯¸ì§€ ë°ì´í„°ë¥¼ ì •ë¦¬
const inputData = $input.first().json;
const imageResponse = inputData;

// ì›ë³¸ ì´ë¯¸ì§€ ë©”íƒ€ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
const batchData = $('Split Image Batch').first().json;

return {
  position: batchData.position,
  prompt: batchData.prompt,
  alt_text: batchData.alt_text,
  caption: batchData.caption,
  placement_reason: batchData.placement_reason,
  index: batchData.index,
  url: imageResponse.data?.[0]?.url || '',
  generated: !!imageResponse.data?.[0]?.url,
  blogData: batchData.blogData
};

// Enhanced HTML ìƒì„± with ë‹¤ì¤‘ ì´ë¯¸ì§€ ì‚½ì…
const blogData = $('Process Enhanced Blog Data').first().json;
const featureImageData = $('Generate Feature Image').first().json;

// Feature image URL
const featureImageUrl = featureImageData.data?.[0]?.url || '';

// ì¸ë¼ì¸ ì´ë¯¸ì§€ ë°ì´í„° (ìˆëŠ” ê²½ìš°ì—ë§Œ)
let inlineImages = [];
const hasInlineImages = $('Has Inline Images?').first().json !== undefined;

if (hasInlineImages) {
  const collectedData = $('Collect Generated Images').first().json;
  inlineImages = collectedData.generated_images || [];
}

// HTMLì— ì´ë¯¸ì§€ ì‚½ì… í•¨ìˆ˜
function insertInlineImages(contentHtml, images) {
  let processedHtml = contentHtml;

  images.forEach(img => {
    if (!img.url || !img.generated) return;

    const imageHtml = `
      <figure class="kg-card kg-image-card kg-width-wide">
        <img src="${img.url}" alt="${img.alt_text}" loading="lazy" width="1000" height="563" />
        <figcaption>${img.caption}</figcaption>
      </figure>
    `;

    // position ê¸°ë°˜ìœ¼ë¡œ ì ì ˆí•œ ìœ„ì¹˜ì— ì‚½ì…
    const position = img.position;
    if (position.includes('after_section_')) {
      const sectionNum = position.split('_')[2];
      const regex = new RegExp(`(<\/h[123456]>)`, 'gi');
      let matchCount = 0;
      processedHtml = processedHtml.replace(regex, (match) => {
        matchCount++;
        if (matchCount === parseInt(sectionNum)) {
          return match + imageHtml;
        }
        return match;
      });
    } else if (position === 'before_conclusion') {
      // ë§ˆì§€ë§‰ ì„¹ì…˜ ì „ì— ì‚½ì…
      const lastH2Index = processedHtml.lastIndexOf('<h2');
      if (lastH2Index > -1) {
        processedHtml = processedHtml.slice(0, lastH2Index) + imageHtml + processedHtml.slice(lastH2Index);
      }
    }
  });

  return processedHtml;
}

// ìµœì¢… HTML ìƒì„±
let finalHtml = blogData.content_html;
if (inlineImages.length > 0) {
  finalHtml = insertInlineImages(finalHtml, inlineImages);
}

// Ghost í˜ì´ë¡œë“œ ìƒì„±
const ghostPayload = {
  posts: [{
    title: blogData.title,
    slug: blogData.slug,
    html: finalHtml,
    feature_image: featureImageUrl,
    feature_image_alt: blogData.title + ' - ëŒ€í‘œ ì´ë¯¸ì§€',
    meta_title: blogData.meta_title,
    meta_description: blogData.meta_description,
    custom_excerpt: blogData.excerpt,
    tags: blogData.tags.map(tag => ({name: tag})),
    status: 'draft',
    visibility: 'public',
    og_title: blogData.meta_title,
    og_description: blogData.meta_description,
    twitter_title: blogData.meta_title,
    twitter_description: blogData.meta_description
  }]
};

return {
  ghostPayload,
  notionPageId: $('Create Notion Page').first().json.id,
  chatId: blogData.chatId,
  messageId: blogData.messageId,
  title: blogData.title,
  slug: blogData.slug,
  totalImages: 1 + inlineImages.length,
  generatedImages: inlineImages.filter(img => img.generated).length
};

